mapper.select:
select *
from steambox.admin
where account = #{account}
  and password = #{password}

mapper.update:
update steambox.admin
set password=#{password}
where account = #{account}

update steambox.community
set name=#{name},
    shoplink=#{shopLink}
where id = #{id}

update steambox.article
set article.hidden=#{hidden}
where id = #{id}

update steambox.user
set forbidden=#{hidden}
where id = #{id}
