mapper.select:
select article.id,article.hidden,article.content,article.img,article.title,article.pubdate,article.creator,article.auditor,article.click_count,article.community_id from steambox.article join steambox.article_category ac on article.id = ac.article_id
hidden = 1
and ac.category_id = #{categoryId}

mapper.update:
update steambox.category
set hidden=#{param2}
where id = #{param1}
